"use strict";(self.webpackChunk_erpproject_music_notes=self.webpackChunk_erpproject_music_notes||[]).push([[4499],{9807:a=>{a.exports=JSON.parse('{"version":{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"tutorialSidebar":[{"type":"link","label":"Bienvenido","href":"/music-notes/","docId":"index","unlisted":false},{"type":"category","label":"Fundamentos","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"\xbfQu\xe9 es el sonido?","href":"/music-notes/fundamentos/el-sonido","docId":"fundamentos/el-sonido","unlisted":false},{"type":"link","label":"Notas musicales","href":"/music-notes/fundamentos/notas-musicales","docId":"fundamentos/notas-musicales","unlisted":false},{"type":"link","label":"Tonos y semitonos","href":"/music-notes/fundamentos/tonos-semitonos","docId":"fundamentos/tonos-semitonos","unlisted":false},{"type":"link","label":"Intervalos","href":"/music-notes/fundamentos/intervalos","docId":"fundamentos/intervalos","unlisted":false},{"type":"link","label":"Clave de la armadura","href":"/music-notes/fundamentos/clave-armadura","docId":"fundamentos/clave-armadura","unlisted":false},{"type":"link","label":"Relatividad","href":"/music-notes/fundamentos/relatividad","docId":"fundamentos/relatividad","unlisted":false}],"href":"/music-notes/category/fundamentos"},{"type":"category","label":"Escalas","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Escala mayor","href":"/music-notes/escalas/escala-mayor","docId":"escalas/escala-mayor","unlisted":false},{"type":"link","label":"Escala menor natural","href":"/music-notes/escalas/escala-menor-natural","docId":"escalas/escala-menor-natural","unlisted":false},{"type":"link","label":"Escala menor arm\xf3nica","href":"/music-notes/escalas/escala-menor-armonica","docId":"escalas/escala-menor-armonica","unlisted":false},{"type":"link","label":"Escala menor mel\xf3dica","href":"/music-notes/escalas/escala-menor-melodica","docId":"escalas/escala-menor-melodica","unlisted":false},{"type":"link","label":"Escala pentat\xf3nica mayor","href":"/music-notes/escalas/escala-pentatonica-mayor","docId":"escalas/escala-pentatonica-mayor","unlisted":false},{"type":"link","label":"Escala pentat\xf3nica menor","href":"/music-notes/escalas/escala-pentatonica-menor","docId":"escalas/escala-pentatonica-menor","unlisted":false},{"type":"link","label":"Escala pentat\xf3nica mayor blues","href":"/music-notes/escalas/escala-pentatonica-mayor-blues","docId":"escalas/escala-pentatonica-mayor-blues","unlisted":false},{"type":"link","label":"Escala pentat\xf3nica menor blues","href":"/music-notes/escalas/escala-pentatonica-menor-blues","docId":"escalas/escala-pentatonica-menor-blues","unlisted":false},{"type":"link","label":"Escala crom\xe1tica","href":"/music-notes/escalas/escala-cromatica","docId":"escalas/escala-cromatica","unlisted":false},{"type":"link","label":"Escala alterada","href":"/music-notes/escalas/escala-alterada","docId":"escalas/escala-alterada","unlisted":false},{"type":"link","label":"Escala tonal","href":"/music-notes/escalas/escala-tonal","docId":"escalas/escala-tonal","unlisted":false},{"type":"link","label":"Escala disminuida","href":"/music-notes/escalas/escala-disminuida","docId":"escalas/escala-disminuida","unlisted":false},{"type":"link","label":"Escala disminuida ST/T","href":"/music-notes/escalas/escala-disminuida-st","docId":"escalas/escala-disminuida-st","unlisted":false}],"href":"/music-notes/category/escalas"},{"type":"category","label":"Armon\xeda I","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Tr\xedadas","href":"/music-notes/armonia-1/triadas","docId":"armonia-1/triadas","unlisted":false},{"type":"link","label":"Inversiones","href":"/music-notes/armonia-1/inversiones","docId":"armonia-1/inversiones","unlisted":false},{"type":"link","label":"Acordes anidados","href":"/music-notes/armonia-1/acordes-anidados","docId":"armonia-1/acordes-anidados","unlisted":false},{"type":"link","label":"Funciones arm\xf3nicas","href":"/music-notes/armonia-1/funcion-armonica","docId":"armonia-1/funcion-armonica","unlisted":false},{"type":"link","label":"Armonizaci\xf3n","href":"/music-notes/armonia-1/armonizacion","docId":"armonia-1/armonizacion","unlisted":false},{"type":"link","label":"Progresiones","href":"/music-notes/armonia-1/progresiones","docId":"armonia-1/progresiones","unlisted":false},{"type":"link","label":"Cadencias","href":"/music-notes/armonia-1/cadencias","docId":"armonia-1/cadencias","unlisted":false}],"href":"/music-notes/category/armon\xeda-i"},{"type":"category","label":"Armon\xeda II","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Cuatriadas","href":"/music-notes/armonia-2/cuatriadas","docId":"armonia-2/cuatriadas","unlisted":false},{"type":"link","label":"Inversiones","href":"/music-notes/armonia-2/inversiones","docId":"armonia-2/inversiones","unlisted":false},{"type":"link","label":"Acordes con tensiones","href":"/music-notes/armonia-2/tensiones","docId":"armonia-2/tensiones","unlisted":false},{"type":"link","label":"Funciones arm\xf3nicas","href":"/music-notes/armonia-2/funcion-armonica","docId":"armonia-2/funcion-armonica","unlisted":false},{"type":"link","label":"Armonizaci\xf3n","href":"/music-notes/armonia-2/armonizacion","docId":"armonia-2/armonizacion","unlisted":false},{"type":"link","label":"Progresiones","href":"/music-notes/armonia-2/progresiones","docId":"armonia-2/progresiones","unlisted":false},{"type":"link","label":"Cadencias","href":"/music-notes/armonia-2/cadencias","docId":"armonia-2/cadencias","unlisted":false}],"href":"/music-notes/category/armon\xeda-ii"},{"type":"category","label":"Armon\xeda III","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Dominantes secundarios","href":"/music-notes/armonia-3/cuatriadas","docId":"armonia-3/cuatriadas","unlisted":false}],"href":"/music-notes/category/armon\xeda-iii"},{"type":"category","label":"Herramientas","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"C\xedrculo de Quintas","href":"/music-notes/herramientas/circulos-de-quintas","docId":"herramientas/circulos-de-quintas","unlisted":false},{"type":"link","label":"Notas en el M\xe1stil","href":"/music-notes/herramientas/fretboard-notes","docId":"herramientas/fretboard-notes","unlisted":false},{"type":"link","label":"Intervalos","href":"/music-notes/herramientas/intervalos","docId":"herramientas/intervalos","unlisted":false},{"type":"link","label":"Armonizador","href":"/music-notes/herramientas/armonizador","docId":"herramientas/armonizador","unlisted":false}],"href":"/music-notes/category/herramientas"},{"type":"link","label":"\ud83d\udc96 Donar v\xeda PayPal","href":"https://paypal.me/anexsoftphp7/3"}]},"docs":{"armonia-1/acordes-anidados":{"id":"armonia-1/acordes-anidados","title":"Acordes anidados","description":"En armon\xeda, los acordes anidados (tambi\xe9n conocidos como acordes add) son aquellos que a\xf1aden una nota adicional \u2014como la novena (9\xaa), oncena (11\xaa) o trecena (13\xaa)\u2014 a una triada, sin incluir la s\xe9ptima.","sidebar":"tutorialSidebar"},"armonia-1/armonizacion":{"id":"armonia-1/armonizacion","title":"Armonizaci\xf3n","description":"La armonizaci\xf3n consiste en construir acordes sobre cada grado de una escala, superponiendo terceras diat\xf3nicas. Este procedimiento permite determinar qu\xe9 tipo de triada corresponde a cada nota dentro del sistema tonal.","sidebar":"tutorialSidebar"},"armonia-1/cadencias":{"id":"armonia-1/cadencias","title":"Cadencias","description":"Las cadencias son f\xf3rmulas arm\xf3nicas que marcan el final o una pausa dentro de una progresi\xf3n. Sirven para dar sentido estructural a la m\xfasica, definiendo cu\xe1ndo una frase se cierra o queda abierta.","sidebar":"tutorialSidebar"},"armonia-1/funcion-armonica":{"id":"armonia-1/funcion-armonica","title":"Funciones arm\xf3nicas","description":"En la armon\xeda tonal, cada acorde cumple una funci\xf3n dentro de la tonalidad. Estas funciones se agrupan en tres categor\xedas:","sidebar":"tutorialSidebar"},"armonia-1/inversiones":{"id":"armonia-1/inversiones","title":"Inversiones","description":"Una inversi\xf3n ocurre cuando el bajo (la nota m\xe1s grave) de un acorde no es la fundamental, sino una de sus otras notas constitutivas. Las inversiones permiten suavizar los movimientos del bajo, enriquecer la armon\xeda y mejorar la conducci\xf3n de voces.","sidebar":"tutorialSidebar"},"armonia-1/progresiones":{"id":"armonia-1/progresiones","title":"Progresiones","description":"Una progresi\xf3n arm\xf3nica es una secuencia de acordes que crea movimiento y direcci\xf3n dentro de una pieza musical. Las progresiones definen el car\xe1cter y la tensi\xf3n de una canci\xf3n.","sidebar":"tutorialSidebar"},"armonia-1/triadas":{"id":"armonia-1/triadas","title":"Tr\xedadas","description":"Una tr\xedada es el acorde m\xe1s b\xe1sico en la m\xfasica tonal. Est\xe1 formado por tres notas: la t\xf3nica, la tercera y la quinta. Estas notas se construyen superponiendo dos intervalos de tercera (pueden ser mayores o menores).","sidebar":"tutorialSidebar"},"armonia-2/armonizacion":{"id":"armonia-2/armonizacion","title":"Armonizaci\xf3n","description":"La armonizaci\xf3n consiste en la construcci\xf3n de acordes a partir de cada grado de una escala mediante la superposici\xf3n de intervalos de tercera. Este proceso permite identificar la funci\xf3n arm\xf3nica de cada grado dentro de una tonalidad, y constituye un pilar fundamental en la teor\xeda musical tonal.","sidebar":"tutorialSidebar"},"armonia-2/cadencias":{"id":"armonia-2/cadencias","title":"Cadencias","description":"Las cadencias son f\xf3rmulas arm\xf3nicas que indican cierres o pausas dentro de una progresi\xf3n. Al usar cuatriadas (acordes con s\xe9ptima), se enriquecen arm\xf3nicamente y ofrecen una sonoridad m\xe1s sofisticada que las triadas.","sidebar":"tutorialSidebar"},"armonia-2/cuatriadas":{"id":"armonia-2/cuatriadas","title":"Cuatriadas","description":"Una cuatriada es un acorde formado por cuatro sonidos, generados por la superposici\xf3n de tres intervalos de tercera diat\xf3nica a partir de una nota fundamental. Se compone de las funciones: fundamental (1\xaa), tercera (3\xaa), quinta (5\xaa) y s\xe9ptima (7\xaa).","sidebar":"tutorialSidebar"},"armonia-2/funcion-armonica":{"id":"armonia-2/funcion-armonica","title":"Funciones arm\xf3nicas","description":"En armon\xeda tonal, los acordes de cuatro sonidos (cuatriadas) permiten una clasificaci\xf3n funcional m\xe1s precisa gracias a la inclusi\xf3n de la s\xe9ptima. Las funciones arm\xf3nicas se agrupan en tres zonas:","sidebar":"tutorialSidebar"},"armonia-2/inversiones":{"id":"armonia-2/inversiones","title":"Inversiones","description":"Una inversi\xf3n ocurre cuando el bajo (nota m\xe1s grave) de un acorde no es la fundamental, sino alguna de las otras notas que lo componen. En el caso de los acordes de cuatro sonidos (estructuras tetr\xe1dicas), existen cuatro posiciones posibles: posici\xf3n fundamental y tres inversiones.","sidebar":"tutorialSidebar"},"armonia-2/progresiones":{"id":"armonia-2/progresiones","title":"Progresiones","description":"Una progresi\xf3n arm\xf3nica es una secuencia de acordes que genera direcci\xf3n tonal. Usar cuatriadas (acordes con s\xe9ptima) aporta mayor riqueza y color a las progresiones comunes.","sidebar":"tutorialSidebar"},"armonia-2/tensiones":{"id":"armonia-2/tensiones","title":"Acordes con tensiones","description":"En armon\xeda, los acordes con tensiones (tambi\xe9n llamados extended chords) son estructuras arm\xf3nicas que a\xf1aden notas m\xe1s all\xe1 de la s\xe9ptima: la novena (9\xaa), oncena (11\xaa) y trecena (13\xaa). Estas tensiones enriquecen el color del acorde y ampl\xedan su espectro expresivo.","sidebar":"tutorialSidebar"},"armonia-3/cuatriadas":{"id":"armonia-3/cuatriadas","title":"Dominantes secundarios","description":"Los dominantes secundarios son acordes que act\xfaan como dominantes temporales de grados distintos al I. Se utilizan para intensificar la llegada a un acorde, aunque ese acorde no sea la t\xf3nica principal. Esta t\xe9cnica ampl\xeda las posibilidades arm\xf3nicas sin cambiar de tonalidad.","sidebar":"tutorialSidebar"},"escalas/escala-alterada":{"id":"escalas/escala-alterada","title":"Escala alterada","description":"La escala alterada, tambi\xe9n conocida como escala superlocria, es el s\xe9ptimo modo de la escala menor mel\xf3dica. Se utiliza especialmente sobre acordes dominantes alterados (7alt), ya que contiene todas las alteraciones posibles en la 9\xaa y la 5\xaa. Es muy com\xfan en el jazz para resolver hacia acordes menores.","sidebar":"tutorialSidebar"},"escalas/escala-cromatica":{"id":"escalas/escala-cromatica","title":"Escala crom\xe1tica","description":"La escala crom\xe1tica incluye los doce semitonos dentro de una octava. A diferencia de las escalas diat\xf3nicas, la crom\xe1tica no sigue una jerarqu\xeda tonal, sino que representa un recorrido completo por todas las notas posibles dentro del sistema de temperamento igual.","sidebar":"tutorialSidebar"},"escalas/escala-disminuida":{"id":"escalas/escala-disminuida","title":"Escala disminuida","description":"La escala disminuida, tambi\xe9n llamada escala octat\xf3nica, es una escala sim\xe9trica de ocho notas que alterna tonos y semitonos, comenzando por un tono. Es ideal para tocar sobre acordes dominantes con tensiones (como 7\u266d9), ya que encaja perfectamente con estructuras alteradas y proporciona m\xfaltiples opciones crom\xe1ticas. Es ampliamente utilizada en el jazz, la m\xfasica de suspenso y progresiones con simetr\xeda.","sidebar":"tutorialSidebar"},"escalas/escala-disminuida-st":{"id":"escalas/escala-disminuida-st","title":"Escala disminuida ST/T","description":"La escala disminuida semitono\u2013tono es una escala sim\xe9trica de ocho notas, tambi\xe9n conocida como escala octat\xf3nica. Se construye alternando semitonos y tonos, comenzando por un semitono. Es ideal para tocar sobre acordes disminuidos (\xb07), ya que contiene todas sus notas y permite generar l\xedneas mel\xf3dicas ricas en tensi\xf3n y simetr\xeda.","sidebar":"tutorialSidebar"},"escalas/escala-mayor":{"id":"escalas/escala-mayor","title":"Escala mayor","description":"La escala mayor es la base del sistema tonal en la m\xfasica occidental. Es una secuencia de siete notas que sigue un patr\xf3n espec\xedfico de tonos y semitonos, a partir del cual se construyen muchas estructuras arm\xf3nicas tradicionales.","sidebar":"tutorialSidebar"},"escalas/escala-menor-armonica":{"id":"escalas/escala-menor-armonica","title":"Escala menor arm\xf3nica","description":"La escala menor arm\xf3nica se deriva de la escala menor natural al elevar el s\xe9ptimo grado un semitono. Esta modificaci\xf3n genera una sonoridad m\xe1s tensa y ex\xf3tica, muy utilizada en la m\xfasica cl\xe1sica, el jazz y g\xe9neros como el metal. Su principal caracter\xedstica es el gran salto entre el sexto y s\xe9ptimo grado, que crea un intervalo de tercera aumentada.","sidebar":"tutorialSidebar"},"escalas/escala-menor-melodica":{"id":"escalas/escala-menor-melodica","title":"Escala menor mel\xf3dica","description":"La escala menor mel\xf3dica es una variante de la menor natural que eleva el sexto y s\xe9ptimo grado un semitono. Esta modificaci\xf3n suaviza el salto caracter\xedstico de la escala menor arm\xf3nica y genera un sonido m\xe1s suave y sofisticado, com\xfan en el jazz y en ciertos pasajes de m\xfasica cl\xe1sica.","sidebar":"tutorialSidebar"},"escalas/escala-menor-natural":{"id":"escalas/escala-menor-natural","title":"Escala menor natural","description":"La escala menor natural es una de las tres formas b\xe1sicas de la escala menor (natural, arm\xf3nica y mel\xf3dica), y se caracteriza por un sonido m\xe1s oscuro o melanc\xf3lico en comparaci\xf3n con la escala mayor. Tambi\xe9n es conocida simplemente como \u201cescala menor\u201d, pero con fines acad\xe9micos es preferible llamarla \u201cescala menor natural\u201d para evitar confusiones.","sidebar":"tutorialSidebar"},"escalas/escala-pentatonica-mayor":{"id":"escalas/escala-pentatonica-mayor","title":"Escala pentat\xf3nica mayor","description":"La escala pentat\xf3nica mayor es una escala de cinco notas derivada de la escala mayor. Suprime el cuarto y el s\xe9ptimo grado, lo que elimina las disonancias m\xe1s comunes y genera un sonido abierto, estable y f\xe1cil de usar en la improvisaci\xf3n. Es muy utilizada en estilos como el rock, blues, country y m\xfasica popular.","sidebar":"tutorialSidebar"},"escalas/escala-pentatonica-mayor-blues":{"id":"escalas/escala-pentatonica-mayor-blues","title":"Escala pentat\xf3nica mayor blues","description":"La escala pentat\xf3nica mayor blues es una variante de la escala pentat\xf3nica mayor, a la que se le a\xf1ade una blue note (tercera menor). Esta combinaci\xf3n da como resultado una mezcla de sonoridad alegre con un toque expresivo y bluesero, t\xedpica en el country, blues tradicional y rock sure\xf1o.","sidebar":"tutorialSidebar"},"escalas/escala-pentatonica-menor":{"id":"escalas/escala-pentatonica-menor","title":"Escala pentat\xf3nica menor","description":"La escala pentat\xf3nica menor es una escala de cinco notas derivada de la escala menor natural. Elimina el segundo y el sexto grado, lo que produce una sonoridad m\xe1s cruda, directa y muy expresiva. Es ampliamente utilizada en g\xe9neros como el blues, rock, funk y metal.","sidebar":"tutorialSidebar"},"escalas/escala-pentatonica-menor-blues":{"id":"escalas/escala-pentatonica-menor-blues","title":"Escala pentat\xf3nica menor blues","description":"La escala pentat\xf3nica menor blues, com\xfanmente llamada simplemente \u201cescala de blues\u201d, es una variante de la pentat\xf3nica menor. Se le a\xf1ade una blue note (quinta disminuida o 5b) que le da su car\xe1cter distintivo, lleno de tensi\xf3n, expresividad y dramatismo. Es una de las escalas m\xe1s utilizadas en el blues, rock y jazz.","sidebar":"tutorialSidebar"},"escalas/escala-tonal":{"id":"escalas/escala-tonal","title":"Escala tonal","description":"La escala tonal, tambi\xe9n conocida como escala de tonos enteros o escala hexat\xf3nica, es una escala sim\xe9trica formada exclusivamente por intervalos de tono. Tiene seis notas y no contiene semitonos, lo que le da un car\xe1cter et\xe9reo, ambiguo y flotante, muy usado en m\xfasica impresionista y moderna (Debussy, por ejemplo).","sidebar":"tutorialSidebar"},"fundamentos/clave-armadura":{"id":"fundamentos/clave-armadura","title":"Clave de la armadura","description":"La clave de la armadura es un conjunto de alteraciones (sostenidos \u266f o bemoles \u266d) que indican qu\xe9 notas estar\xe1n modificadas en una escala o tonalidad determinada.","sidebar":"tutorialSidebar"},"fundamentos/el-sonido":{"id":"fundamentos/el-sonido","title":"\xbfQu\xe9 es el sonido?","description":"Antes de hablar de escalas, acordes o intervalos, es fundamental entender qu\xe9 es el sonido y c\xf3mo lo percibimos. La m\xfasica parte del sonido, y todo lo que analizamos en teor\xeda musical se basa en sus propiedades f\xedsicas y perceptivas.","sidebar":"tutorialSidebar"},"fundamentos/intervalos":{"id":"fundamentos/intervalos","title":"Intervalos","description":"Un intervalo es la distancia entre dos notas. Es uno de los conceptos m\xe1s importantes de la teor\xeda musical porque a partir de \xe9l se construyen las escalas, los acordes y la armon\xeda funcional.","sidebar":"tutorialSidebar"},"fundamentos/notas-musicales":{"id":"fundamentos/notas-musicales","title":"Notas musicales","description":"La m\xfasica occidental se basa en un sistema de 12 sonidos organizados de forma c\xedclica. Este conjunto de sonidos constituye la base de todo lo que se desarrolla en teor\xeda musical: escalas, intervalos, acordes, progresiones y modos.","sidebar":"tutorialSidebar"},"fundamentos/relatividad":{"id":"fundamentos/relatividad","title":"Relatividad","description":"Una tonalidad relativa es aquella que comparte exactamente las mismas notas (y por tanto la misma armadura), pero tiene diferente nota principal o t\xf3nica.","sidebar":"tutorialSidebar"},"fundamentos/tonos-semitonos":{"id":"fundamentos/tonos-semitonos","title":"Tonos y semitonos","description":"Para construir escalas, analizar intervalos y comprender c\xf3mo se mueven las notas en el sistema musical, es esencial entender qu\xe9 son los tonos y semitonos. Estas son las unidades de distancia m\xe1s b\xe1sicas entre sonidos.","sidebar":"tutorialSidebar"},"herramientas/armonizador":{"id":"herramientas/armonizador","title":"Armonizador","description":"Muestra los acordes que pertenecen a una escala seleccionada, organizados por grado y funci\xf3n arm\xf3nica. Permite elegir la tonalidad y el modo (mayor o menor) para visualizar su armonizaci\xf3n diat\xf3nica.","sidebar":"tutorialSidebar"},"herramientas/circulos-de-quintas":{"id":"herramientas/circulos-de-quintas","title":"C\xedrculo de Quintas","description":"El C\xedrculo de Quintas es una herramienta visual fundamental en la teor\xeda musical. Organiza las tonalidades mayores y menores seg\xfan la cantidad de alteraciones (sostenidos \u266f o bemoles \u266d) que contienen, facilitando la comprensi\xf3n de las relaciones arm\xf3nicas.","sidebar":"tutorialSidebar"},"herramientas/fretboard-notes":{"id":"herramientas/fretboard-notes","title":"Notas en el M\xe1stil","description":"<TabBlock","sidebar":"tutorialSidebar"},"herramientas/intervalos":{"id":"herramientas/intervalos","title":"Intervalos","description":"","sidebar":"tutorialSidebar"},"index":{"id":"index","title":"Bienvenido","description":"\xbfQu\xe9 es esto?","sidebar":"tutorialSidebar"}}}}')}}]);